import React, { useState, useEffect } from "react";
import Login from "./components/Login";
import Register from "./components/Register";
import Dashboard from "./components/Dashboard";
import Navigation from "./components/Navigation";
import UserManagement from "./components/UserManagement";
import UserReports from "./components/UserReports";
import IssueManagement from "./components/IssueManagement";
import ReservationManagement from "./components/ReservationManagement";
import { getJwt } from "./api/api"; // Import the JWT helper

function App() {
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("dashboard");

  // Render the active component based on the selected tab
  const renderActiveComponent = () => {
    switch (activeTab) {
      case "dashboard":
        return <Dashboard user={user} onLogout={handleLogout} />;
      case "users":
        return <UserManagement user={user} />;
      case "reports":
        return <UserReports user={user} />;
      case "issues":
        return <IssueManagement user={user} />;
      case "reservations":
        return <ReservationManagement user={user} />;
      default:
        return <Dashboard user={user} onLogout={handleLogout} />;
    }
  };

  const handleLogout = () => {
    setUser(null);
    setActiveTab("dashboard");
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {!user ? (
        <div className="flex flex-col items-center justify-center min-h-screen p-4">
          {showRegister
            ? <Register onRegister={setUser} />
            : <Login onLogin={setUser} />}
          <button
            onClick={()=>setShowRegister(!showRegister)}
            className="mt-4 text-blue-600 hover:text-blue-800 underline transition duration-200"
          >
            {showRegister ? "Back to Login" : "Register"}
          </button>
        </div>
      ) : (
        <div>
          <Navigation 
            activeTab={activeTab} 
            onTabChange={setActiveTab} 
            user={user} 
          />
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
            {renderActiveComponent()}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
