import React, { useState, useEffect } from "react";
import { getBooks, addBook, deleteBook, issueBook, reserveBook, getUserIssues, getUserReservations } from "../api/api";
import BookList from "./BookList";
import BookSearch from "./BookSearch";

export default function Dashboard({ user, onLogout }) {
  const [books, setBooks] = useState([]);
  const [showAdd, setShowAdd] = useState(false);
  const [form, setForm] = useState({title:"",author:"",isbn:"",genre:"",edition:"",publisher:""});
  const [error, setError] = useState("");
  const [stats, setStats] = useState({
    currentIssues: 0,
    activeReservations: 0,
    booksOverdue: 0
  });

  useEffect(() => {
    // Load books
    getBooks().then(result => {
      if (Array.isArray(result)) {
        setBooks(result);
        setError("");
      } else {
        setBooks([]);
        setError(result.error || "Failed to load books");
      }
    });
    
    // Load user stats
    const fetchUserStats = async () => {
      try {
        // Get user issues
        const issues = await getUserIssues(user.id);
        if (Array.isArray(issues)) {
          const currentIssues = issues.filter(issue => !issue.returnDate).length;
          const booksOverdue = issues.filter(issue => {
            if (issue.returnDate) return false;
            const dueDate = new Date(issue.dueDate);
            const today = new Date();
            return today > dueDate;
          }).length;
          
          // Get reservations
          const reservations = await getUserReservations(user.id);
          if (Array.isArray(reservations)) {
            const activeReservations = reservations.filter(res => res.active).length;
            
            setStats({
              currentIssues,
              activeReservations,
              booksOverdue
            });
          }
        }
      } catch (err) {
        console.error("Failed to load user stats", err);
      }
    };
    
    fetchUserStats();
  }, [user.id]);

  const handleSearchResults = results => setBooks(results);

  const handleAddBook = async e => {
    e.preventDefault();
    await addBook(form);
    setForm({title:"",author:"",isbn:"",genre:"",edition:"",publisher:""});
    setShowAdd(false);
    getBooks().then(result => {
      if (Array.isArray(result)) {
        setBooks(result);
        setError("");
      } else {
        setBooks([]);
        setError(result.error || "Failed to load books");
      }
    });
  };

  const handleDelete = async id => {
    await deleteBook(id);
    getBooks().then(result => {
      if (Array.isArray(result)) {
        setBooks(result);
        setError("");
      } else {
        setBooks([]);
        setError(result.error || "Failed to load books");
      }
    });
  };

  const handleBorrow = async (bookId) => {
    const result = await issueBook(bookId, user.id);
    if (result && !result.error) {
      alert("Book borrowed successfully!");
      getBooks().then(result => {
        if (Array.isArray(result)) {
          setBooks(result);
          setError("");
        } else {
          setBooks([]);
          setError(result.error || "Failed to load books");
        }
      });
    } else {
      alert(result.error || "Failed to borrow book");
    }
  };

  const handleReserve = async (bookId) => {
    const result = await reserveBook(bookId, user.id);
    if (result && !result.error) {
      alert("Book reserved successfully!");
      getBooks().then(result => {
        if (Array.isArray(result)) {
          setBooks(result);
          setError("");
        } else {
          setBooks([]);
          setError(result.error || "Failed to load books");
        }
      });
    } else {
      alert(result.error || "Failed to reserve book");
    }
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-xl shadow-lg p-6 mb-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          <h2 className="text-2xl font-bold text-gray-800">
            Welcome, {user.name} 
            <span className="text-lg font-normal text-gray-500 ml-2">({user.role})</span>
          </h2>
          <button 
            onClick={onLogout} 
            className="bg-gray-600 text-white py-2 px-4 rounded-lg font-medium hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 transition duration-200"
          >
            Logout
          </button>
        </div>
      </div>

      {/* User Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <div className="bg-blue-50 rounded-xl shadow-md p-6 flex flex-col items-center justify-center text-center">
          <div className="text-blue-500 text-3xl font-bold">{stats.currentIssues}</div>
          <div className="text-blue-700 mt-2 font-medium">Books Currently Borrowed</div>
        </div>
        <div className="bg-purple-50 rounded-xl shadow-md p-6 flex flex-col items-center justify-center text-center">
          <div className="text-purple-500 text-3xl font-bold">{stats.activeReservations}</div>
          <div className="text-purple-700 mt-2 font-medium">Active Reservations</div>
        </div>
        <div className={`${stats.booksOverdue > 0 ? 'bg-red-50' : 'bg-green-50'} rounded-xl shadow-md p-6 flex flex-col items-center justify-center text-center`}>
          <div className={`${stats.booksOverdue > 0 ? 'text-red-500' : 'text-green-500'} text-3xl font-bold`}>{stats.booksOverdue}</div>
          <div className={`${stats.booksOverdue > 0 ? 'text-red-700' : 'text-green-700'} mt-2 font-medium`}>
            {stats.booksOverdue > 0 ? 'Books Overdue' : 'No Overdue Books'}
          </div>
        </div>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}
      <BookSearch onResults={handleSearchResults} />
      <BookList books={books} onDelete={handleDelete} user={user} onBorrow={handleBorrow} onReserve={handleReserve} />
      {user.role !== "STUDENT" && (
        <div className="bg-white rounded-xl shadow-lg p-6 mt-6">
          <button 
            onClick={()=>setShowAdd(!showAdd)} 
            className="bg-green-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 transition duration-200 transform hover:scale-105"
          >
            {showAdd ? "Cancel" : "Add New Book"}
          </button>
          {showAdd && (
            <form onSubmit={handleAddBook} className="mt-6 grid grid-cols-1 md:grid-cols-2 gap-4">
              <input 
                placeholder="Book Title" 
                value={form.title} 
                onChange={e=>setForm({...form, title:e.target.value})} 
                required 
                className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200" 
              />
              <input 
                placeholder="Author Name" 
                value={form.author} 
                onChange={e=>setForm({...form, author:e.target.value})} 
                required 
                className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200" 
              />
              <input 
                placeholder="ISBN Number" 
                value={form.isbn} 
                onChange={e=>setForm({...form, isbn:e.target.value})} 
                required 
                className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200" 
              />
              <input 
                placeholder="Genre" 
                value={form.genre} 
                onChange={e=>setForm({...form, genre:e.target.value})} 
                required 
                className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200" 
              />
              <input 
                placeholder="Edition" 
                value={form.edition} 
                onChange={e=>setForm({...form, edition:e.target.value})} 
                className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200" 
              />
              <input 
                placeholder="Publisher" 
                value={form.publisher} 
                onChange={e=>setForm({...form, publisher:e.target.value})} 
                className="px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition duration-200" 
              />
              <div className="md:col-span-2">
                <button 
                  type="submit" 
                  className="w-full bg-blue-600 text-white py-3 px-6 rounded-lg font-medium hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition duration-200"
                >
                  Add Book to Library
                </button>
              </div>
            </form>
          )}
        </div>
      )}
    </div>
  );
}
